		// Select Custom
	const SelectCustom = document.querySelector('.select-custom');
	const SelectDropdown = document.querySelector('.dropdown');
	SelectCustom.addEventListener('click', () => {
		SelectCustom.classList.toggle('is-active')
		SelectDropdown.classList.toggle('is-active')
	});
	window.addEventListener('click', e => {
		const target = e.target
		if (!target.closest('.dropdown.is-active') && !target.closest('.select-custom')) {
			SelectDropdown.classList.remove('is-active')
			SelectCustom.classList.remove('is-active')
		}
	});