		// Select Custom
		const SelectCustom = document.querySelector('.select-custom');
		const SelectDropdown = document.querySelector('.dropdown');
		SelectCustom.addEventListener('click', () => {
			SelectCustom.classList.toggle('is-active')
			SelectDropdown.classList.toggle('is-active')
		});
		window.addEventListener('click', e => {
			const target = e.target
			if (!target.closest('.dropdown.is-active') && !target.closest('.select-custom')) {
				SelectDropdown.classList.remove('is-active')
				SelectCustom.classList.remove('is-active')
			}
		});

		// Open Filters Mobile
		const Filters = document.querySelector('.filters');
		const FilterMobOpen = document.querySelector('.filters__mopen');
		const FiltersMobClose = document.querySelector('.filters__mclose');
		FilterMobOpen.addEventListener('click', () => {
			Filters.classList.toggle('is-active')
			document.body.classList.toggle('dis-scroll')
		});
		FiltersMobClose.addEventListener('click', () => {
			Filters.classList.remove('is-active')
			document.body.classList.remove('dis-scroll')
		});

		// Open Podcategory Dropdown
		const PodcategoryItem = document.querySelector('.podcategories__item');
		const PodcategoryDropdown = document.querySelector('.podcategories__dropdown');
		PodcategoryItem.addEventListener('click', () => {
			PodcategoryItem.classList.toggle('is-active')
			PodcategoryDropdown.classList.toggle('is-active')
		});
		window.addEventListener('click', e => {
			const target = e.target
			if (!target.closest('.dropdown.is-active') && !target.closest('.podcategories__item')) {
				PodcategoryItem.classList.remove('is-active')
				PodcategoryDropdown.classList.remove('is-active')
			}
		});


		// Переключение вида карточек товара
		// Проходим формы
		$('.layout-btn--list').on('click', function () {
			$('.tovars-grid').addClass('tovars-list')
			$('.card-tovar').addClass('card-tovar--row')
			$('.layout-btn--grid').removeClass('is-active')
			$('.layout-btn--list').addClass('is-active')
			return false;
		});
		$('.layout-btn--grid').on('click', function () {
			$('.tovars-grid').removeClass('tovars-list')
			$('.card-tovar').removeClass('card-tovar--row')
			$('.layout-btn--list').removeClass('is-active')
			$('.layout-btn--grid').addClass('is-active')
			return false;
		});